function InitializePage(){DisplayGreetingMessage(),DisplayCartCount(),SetupAccountButton()}function SetupAccountButton(){const t=document.getElementById("accBtn"),e=GetLoggedInUser();t.setAttribute("href",e?"/Bootstrap-Ecommerce/pages/userAccount.html":"/Bootstrap-Ecommerce/pages/login.html")}function GetRelativePath(t){const e=window.location.pathname;if(e.includes("index.html")){return`./${t}`}t.split("/").pop();return`./${t.split("/").pop()}`}function DisplayGreetingMessage(){const t=document.getElementById("userGreeting"),e=GetLoggedInUser();e?(t.textContent=`Welcome ${e.firstName}`,t.classList.remove("d-none")):(t.textContent="",t.classList.add("d-none"))}function DisplayCartCount(){const t=document.getElementById("cartCount"),e=localStorage.getItem("numberOfItemsInCart")||0;t.textContent=e}function GetLoggedInUser(){const t=JSON.parse(localStorage.getItem("usersDetails"))||[];return console.log(t),t.find(t=>"true"===t.loggedIn)}InitializePage();