function displayBikeProductItems(t) {
  const e = document.getElementById("products-container");
  t.forEach((t) => {
    const n = document.createElement("div");
    n.setAttribute("class", "col-md-6 col-lg-4");
    const a = document.createElement("div");
    a.setAttribute("class", "card shadow");
    const c = document.createElement("img");
    (c.src = t.image_source),
      (c.alt = t.model_name),
      c.setAttribute("class", "card-img-top"),
      c.setAttribute("alt", "Card image caption");
    const o = document.createElement("div");
    o.setAttribute("class", "card-body");
    const s = document.createElement("h5");
    (s.textContent = `${t.brand} ${t.model_name}`),
      s.setAttribute("class", "card-title fw-bold");
    const d = document.createElement("p");
    (d.textContent = `${t.short_description}...`),
      d.setAttribute("class", "card-text text-secondary");
    const r = document.createElement("div"),
      l = document.createElement("span");
    (l.textContent = "Year of Make : "), l.setAttribute("class", "fw-bold");
    const i = document.createElement("span");
    (i.textContent = `${t.year_of_make}`),
      i.setAttribute("class", "text-secondary"),
      r.setAttribute("class", "card-text "),
      r.append(l, i);
    const m = document.createElement("div"),
      u = document.createElement("span");
    (u.textContent = "Price : "), u.setAttribute("class", "fw-bold");
    const p = document.createElement("span");
    (p.innerHTML = `${t.price} &euro;`),
      p.setAttribute("class", "fw-bold text-success"),
      m.setAttribute("class", "card-text mt-3 mb-3"),
      m.append(u, p);
    const C = document.createElement("button");
    (C.type = "button"),
      (C.textContent = "Add to cart"),
      C.setAttribute("class", "btn btn-dark"),
      C.addEventListener(
        "click",
        function () {
          AddToCart(t);
        },
        !1
      ),
      e.appendChild(a),
      e.append(n),
      n.appendChild(a),
      a.appendChild(c),
      a.appendChild(o),
      o.appendChild(s),
      o.appendChild(d),
      o.appendChild(r),
      o.appendChild(m),
      o.appendChild(C);
  });
}
function AddToCart(t) {
  let e = parseInt(localStorage.getItem("numberOfItemsInCart")) || 0;
  e++, localStorage.setItem("numberOfItemsInCart", e), DisplayCartCount();
  let n = JSON.parse(localStorage.getItem("currentCartItems")) || [],
    a = n.findIndex((e) => e.id === t.id);
  -1 !== a ? (n[a].quantity += 1) : n.push({ ...t, quantity: 1 }),
    console.log(n),
    localStorage.setItem("currentCartItems", JSON.stringify(n));
}
function DisplayCartCount() {
  const t = document.getElementById("cartCount"),
    e = localStorage.getItem("numberOfItemsInCart") || 0;
  t.textContent = e;
}
document.addEventListener("DOMContentLoaded", async () => {
  const t = document.createElement("div");
  document.body.appendChild(t);
  try {
    const t = await fetch("../source/bikes.json"),
      e = await t.json();
    displayBikeProductItems(e);
  } catch (t) {
    console.error(t);
  }
});
